        <span>Names</span>
        <div className='checkedItems'>
          {selectedItems.map((item) => (
            <>
            <div key={item.id} className='checkedItem'>{item.name} <button key={item.id} className='deleteBtn' onClick={()=>handleDelete(item.id) }>x</button></div>

            </>
