import React, { useEffect, useState } from 'react';
import './style.scss';

const SelectableItems = () => {
  const [data, setData] = useState([]);
  const [checkedItems, setCheckedItems] = useState({});

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('https://jsonplaceholder.typicode.com/users');
        const data = await response.json();
        setData(data);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);

  const handleItemClick = (itemId) => {
    setCheckedItems((prevCheckedItems) => ({
      ...prevCheckedItems,
      [itemId]: !prevCheckedItems[itemId]
    }));
  };

  const selectedItems = data.filter((item) => checkedItems[item.id]);

  return (
    <>
       <div className="selectedItems">
        <div className='checkedItems'>
          {selectedItems.map((item) => (
            <>
            <div key={item.id} className='checkedItem'>{item.name} <button className='deleteBtn'>X</button></div>

              </>
            ))}
        </div>
      </div>
      <div className='dropDown'>
        {data.map((el) => (
          <div className="item" key={el.id}>
            <div className='itemData'>
              <span>{el.id}</span>
              <p>{el.name}</p>
            </div>
            <input
              type="checkbox"
              className='check'
              checked={checkedItems[el.id] || false}
              onChange={() => handleItemClick(el.id)}
            />
          </div>
        ))}
      </div>


    </>
  );
};

export default SelectableItems;
