import './style.scss';
import React, { useState } from 'react';

const SelectableInputs = () => {
  const [selectedInputs, setSelectedInputs] = useState([]);

  const inputData = [
    { id: 1, label: "Option 1" },
    { id: 2, label: "Option 2" },
    { id: 3, label: "Option 3" },
    // Add more items as needed
  ];
  const handleInputChange = (event) => {
    const { value, checked } = event.target;
    if (checked) {
      setSelectedInputs([...selectedInputs, value]);
    } else {
      setSelectedInputs(selectedInputs.filter((input) => input !== value));
    }
  };

  return (
    <div>
      {inputData.map((input) => (
        <label key={input.id}>
          <input
            type="checkbox"
            value={input.id}
            checked={selectedInputs.includes(input.id)}
            onChange={handleInputChange}
          />
          {input.label}
        </label>
      ))}
    </div>
  );
};

export default SelectableInputs;
