import React, { useState } from 'react';

const SelectAndDelete = () => {
  const [people, setPeople] = useState([
    { id: 1, name: 'John' },
    { id: 2, name: 'Jane' },
    { id: 3, name: 'Bob' },
    // Add more people as needed
  ]);

  const [selectedPeople, setSelectedPeople] = useState([]);

  const handleSelect = (id) => {
    if (selectedPeople.includes(id)) {
      setSelectedPeople(selectedPeople.filter((personId) => personId !== id));
    } else {
      setSelectedPeople([...selectedPeople, id]);
    }
  };

  const handleDeleteSelected = () => {
    const filteredPeople = people.filter((person) => !selectedPeople.includes(person.id));
    setPeople(filteredPeople);
    setSelectedPeople([]);
  };

  return (
    <div>
      <h2>Select and Delete People</h2>
      <ul>
        {people.map((person) => (
          <li key={person.id}>
            <label>
              <input
                type="checkbox"
                checked={selectedPeople.includes(person.id)}
                onChange={() => handleSelect(person.id)}
              />
              {person.name}
            </label>
          </li>
        ))}
      </ul>
      <button onClick={handleDeleteSelected} disabled={selectedPeople.length === 0}>
        Delete Selected
      </button>
    </div>
  );
};

export default SelectAndDelete;
